    private boolean validar(){
    //metodo para validar txtVuelo longitud 7 caracteres.
    if(!new TxtValidator(txtVuelo).length(7)) return false;
    // validar txtCantidad entero entre 40 y 400 plazas
    if(!new TxtValidator(txtCantidad).isInteger(40, 400)) return false;
        return true;
    }
    
    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed
        // Evento Agregar(va a dar de alta un vuelo)
        if (validar()){
            Vuelo vuelo = new Vuelo(
                    txtVuelo.getText(),
                    Integer.parseInt(txtCantidad.getText())
            );
            JOptionPane.showMessageDialog(this, "Se cre√≥ un vuelo!");
            txtVuelo.setText("");
            txtCantidad.setText("");
            txtVuelo.requestFocus();
        }
    }//GEN-LAST:event_btnAgregarActionPerformed

